import React, { Component } from "react";
import styled from "styled-components";
import trashBtn from "./delete.png";

export const TaskWrapper = styled.div`
  width: clamp(300px, 25vw, 500px);
  border-radius: 5px;
  padding: 0 0.2rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: relative;

  ::after {
    content: "";
    width: 100%;
    height: 5px;
    display: block;
    position: absolute;
    bottom: -0.5rem;
    background-color: black;
    background: linear-gradient(to right, blueviolet 50%, #ff3d7f 50%);
    background-size: 0;
    background-repeat: no-repeat;
    background-position: center;
    transition: all 0.35s ease-out;
  }

  :hover::after {
    background-position: right;
    background-size: 200%;
  }

  ul {
    width: 85%;
    list-style: none;
  }

  ul > li {
    font-size: clamp(0.9rem, 1.5vw, 2rem);
    font-family: "Source Sans Pro", sans-serif;
    word-wrap: break-word;
  }

  button {
    width: 35px;
    height: 35px;
    border: 2px solid black;
    border-radius: 50%;
    background-color: red;
    cursor: pointer;
  }

  img {
    width: 20px;
    height: 20px;
  }
`;

export default class App extends Component {
  state = {
    task: "",
    list: [],
  };

  add = () => {
    if (this.state.task !== "" && !this.state.task.match(/^[ \t]+$/)) {
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now(),
        }),
        task: "",
      });
    }
  };

  remove = (id) => {
    this.setState({
      list: this.state.list.filter((item) => item.id !== id),
    });
  };

  render() {
    return (
      <>
        {this.state.list.map((item) => (
          <TaskWrapper>
            <ul>
              <li>{item.task}</li>
            </ul>
            <button onClick={() => this.remove(item.id)}>
              <img src={trashBtn} alt="trash" />
            </button>
          </TaskWrapper>
        ))}
      </>
    );
  }
}
