import React, {Component} from "react";
import styled from "styled-components";
import "./App.css";
// import newBtn from './add.png';
import newBtn from './add_01.jpg';
import trashBtn from './delete.png';

export const Wrapper = styled.section`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const InputWrapper = styled.div`
  display: flex;
  align-items: center;
  gap: .2rem;

  input{
    outline: none;
  }

  button{
    width: 20px;
    height: 20px;
    background-color: limegreen;
    border: none;
    cursor: pointer;
  }

  img{
    width: 20px;
    height: 20px;
    border-radius: 50%;

  }
`;

export const TaskWrapper = styled.div`
  display: flex;

  ul{
    list-style: none;
  }

  button{
    width: 20px;
    height: 20px;
    background-color: red;
    border: none;
    cursor: pointer;
  }

  img{
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: red;
  }
`;

export default class App extends Component{

  state = {
    task: "",
    list: []
  }

  handleChange = (e) => {
    this.setState({
      task: e.target.value
    })
  }

  enter = (e) => {
    if (this.state.task.length > 0 && e.key === "Enter"){
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now()
        }),
        task: "",  
      })
    }
  }

  add = () => {
    if (this.state.task !== "" && !this.state.task.match(/^[ \t]+$/)){
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now()
        }),
        task: "",  
      })
    }
  }

  remove = (id) => {
    this.setState({
      list: this.state.list.filter((item) => item.id !== id)
    })
  }

  render(){
    return(
      <Wrapper>
        <h1>Lista de Tarefas</h1>
        <InputWrapper>
          <input onChange={this.handleChange} value={this.state.task}
          onKeyPress={this.enter}
          />
          <button onClick={this.add}>
            <img src={newBtn} alt="add"/>
          </button>
        </InputWrapper>
        {this.state.list.map((item) => (
          <TaskWrapper>
            <ul>
              <li>{item.task}</li>
            </ul>
            <button onClick={() => this.remove(item.id)}>
              <img src={trashBtn} alt="trash"/>
            </button>
          </TaskWrapper>
        ))}
      </Wrapper>
    )
  }
}
