import React, {Component} from "react";
import styled from "styled-components";
import "./App.css";
import newBtn from './add.png';
import trash from './delete.png';

export const Wrapper = styled.section`
  width: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export default class App extends Component{

  state = {
    task: "",
    list: []
  }

  handleChange = (e) => {
    this.setState({
      task: e.target.value
    })
  }

  enter = (e) => {
    if (this.state.task.length > 0 && e.key === "Enter"){
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now()
        }),
        task: "",  
      })
    }
  }

  add = () => {
    if (this.state.task !== "" && !this.state.task.match(/^[ \t]+$/)){
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now()
        }),
        task: "",  
      })
    }
  }

  remove = (id) => {
    this.setState({
      list: this.state.list.filter((item) => item.id !== id)
    })
  }

  render(){
    return(
      <Wrapper>
        <h1>Lista de Tarefas</h1>
        <div>
          <input onChange={this.handleChange} value={this.state.task}
          onKeyPress={this.enter}
          />
          <button onClick={this.add}>
            <img src={newBtn} alt="add"/>
          </button>
        </div>
        {this.state.list.map((item) => (
          <div>
            <ul>
              <li>{item.task}</li>
            </ul>
            <button onClick={() => this.remove(item.id)}>
              <img src={trash} alt="trash"/>
            </button>
          </div>
        ))}
      </Wrapper>
    )
  }
}
