import React, {Component} from "react";
import styled from "styled-components";
import "./App.css";
import newBtn from './add.png';
import trashBtn from './delete.png';

export const Wrapper = styled.section`
  max-width: 1920px;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;

  h1{
    font-size: clamp(2rem, 5vw, 4rem);
    font-family: 'Caveat', cursive;
  }
`;

export const InputWrapper = styled.div`
  width: clamp(300px, 25vw, 500px);
  border-radius: 5px;
  padding: 0 .2rem;
  background-color: bisque;
  display: flex;
  align-items: center;
  justify-content: center;
  gap: .5rem;

  input{
    width: clamp(300px, 25vw, 500px);
    height: 35px;
    border-radius: 10px;
    font-size: clamp(1rem, 1.5vw, 2rem);
    font-family: 'Source Sans Pro', sans-serif;
    outline: none;
  }

  button{
    width: 35px;
    height: 35px;
    background-color: transparent;
    border: none;
    cursor: pointer;
  }

  img{
    width: 35px;
    height: 35px;
    border-radius: 50%;
    background-color: limegreen;
  }
`;

export const TaskWrapper = styled.div`
  width: clamp(300px, 25vw, 500px);
  background-color: aqua;
  border-radius: 5px;
  padding: 0 .2rem;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: relative;

  ::after{
    content: '';
    width: 0%;
    height: 5px;
    display: block;
    position: absolute;
    left: -50%;
    bottom: -.5rem;
    background-color: black;
    background: linear-gradient(to right, black 50%, #d74430 50%);
    background-size: 100%;
    transition: 0.5s ease-out;
  }

  :hover::after{
    width: 100%;
    left: 0;
    background-position: right;
    background-color: #d74430;
    transition: 0.1s ease-out;
  }

  ul{
    width: 85%;
    list-style: none;
  }

  ul > li{
    font-size: clamp(.9rem, 1.5vw, 2rem);
    font-family: 'Source Sans Pro', sans-serif;
    word-wrap: break-word;
  }

  button{
    width: 35px;
    height: 35px;
    border: 2px solid black;
    border-radius: 50%;
    background-color: red;
    cursor: pointer;
    transition: 0.2s ease-out;
    transform: scale(1,0);
  }

  ul:focus-within ~ button,
  ul:hover ~ button{
    transform: scale(1,1);
  }

  li:focus-within ~ button,
  li:hover ~ button{
    transform: scale(1,1);
  }

  img{
    width: 20px;
    height: 20px;
  }
`;

export default class App extends Component{

  state = {
    task: "",
    list: []
  }

  handleChange = (e) => {
    this.setState({
      task: e.target.value
    })
  }

  enter = (e) => {
    if (this.state.task.length > 0 && e.key === "Enter"){
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now()
        }),
        task: "",  
      })
    }
  }

  add = () => {
    if (this.state.task !== "" && !this.state.task.match(/^[ \t]+$/)){
      this.setState({
        list: this.state.list.concat({
          task: this.state.task,
          id: Date.now()
        }),
        task: "",  
      })
    }
  }

  remove = (id) => {
    this.setState({
      list: this.state.list.filter((item) => item.id !== id)
    })
  }

  render(){
    return(
      <Wrapper>
        <h1>Lista de Tarefas</h1>
        <InputWrapper>
          <input onChange={this.handleChange} value={this.state.task}
          onKeyPress={this.enter}
          />
          <button onClick={this.add}>
            <img src={newBtn} alt="add"/>
          </button>
        </InputWrapper>
        {/* {this.state.list.map((item) => (
          <TaskWrapper>
            <ul>
              <li>{item.task}</li>
            </ul>
            <button onClick={() => this.remove(item.id)}>
              <img src={trashBtn} alt="trash"/>
            </button>
          </TaskWrapper>
        ))} */}
          <TaskWrapper>
            <ul>
              <li>aaaaaaaaaaa</li>
            </ul>
            <button>
              <img src={trashBtn} alt="trash"/>
            </button>
          </TaskWrapper>
      </Wrapper>
    )
  }
}
